chrome.storage.local.get("apps",a=>{a.apps.forEach(a=>{$(".apps").append(`<li><img src="${a.icon?a.icon:"img/404.png"}"><div class="app-info"><h3>${a.name}</h3><span class="url">${a.url}</span></div><div class="actions"><i class="fa fa-${a.enabled?"check":"uncheck"}" data-name="${a.name}"></i></div></li>`)})}),document.querySelector("body").addEventListener("click",function(a){if("i"===a.target.tagName.toLowerCase()){let e=$(a.target).attr("data-name");import("/functions.mjs").then(s=>{chrome.storage.local.get("apps",n=>{let c=s.GetAppByName(n.apps,e),t=$(a.target).hasClass("fa-check");t?$(a.target).removeClass("fa-check").addClass("fa-uncheck"):$(a.target).removeClass("fa-uncheck").addClass("fa-check"),s.UpdateApp(n.apps,c,!t)})})}});